package com.soecode.lyf.enums;/** * 定时监控运行状态枚举类* @author  created by liujie* @date    2017年3月10日 --- @上午11:13:44* */public enum ScheduleExecuteStatus {    RUNNING(0, "正在运行"),    FINISHED(1, "已经完成"),    EXCEPTION(2, "出现异常结束");    private int code;    private String desc;    ScheduleExecuteStatus(int code, String desc) {        this.code = code;        this.desc = desc;    }	public int getCode() {		return code;	}	public void setCode(int code) {		this.code = code;	}	public String getDesc() {		return desc;	}	public void setDesc(String desc) {		this.desc = desc;	}}