package com.soecode.lyf.monitor;import org.quartz.JobExecutionContext;import org.quartz.JobExecutionException;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import java.text.SimpleDateFormat;import java.util.Calendar;/** * * @author  created by liujie* @date    2017年3月10日 --- @下午1:41:13* */public class ExampleScheduledJob extends AbstractScheduleJob {	private Logger logger = LoggerFactory.getLogger(this.getClass());    @Override    public void schedule(JobExecutionContext context) throws JobExecutionException {        String jobName = context.getJobDetail().getKey().getName();        SimpleDateFormat dateFormat = new SimpleDateFormat("yyyy-MM-dd HH:mm:ss");        logger.info("任务Key: {}, 正在执行，执行时间: {}", jobName, dateFormat.format(Calendar.getInstance().getTime()));        try {            Thread.sleep(10000);        } catch (Throwable t) {        }    }    @Override    public String scheduleId() {        return "schedule.test";    }    @Override    public String groupId() {        return "group.test";    }}